package assessment1;

public class Typecasting {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
byte b=10;
int i=b;
System.out.println(i);

float f=22.14f;
double d=f;
System.out.println(d);

char ch='A';
int i3=ch;
System.out.println(i3);

double d1=10.52f;
System.out.println(d1);
long l=(long)d1;

float f2=34.56f;
int i2=(int)f2;
System.out.println(i2);


	}

}
